import React, { useState, useEffect } from "react";
import ProfileImage from "../images/Vector.png";
import ProfileRedaction from "../images/Redaction.svg";
import AddPhoto from "../images/Add.svg";
import Card from "./Card";
import api from "../utils/api";

function Main({onEditAvatar, onEditProfile, onAddPlace, onCardClick, userName, userAvatar, userDescription }) {
  const [cards, setCards] = useState([]);
  useEffect(() => {
    api.getUserProfile()
      .then((res) => {
        setInfoUser(res);
      })
      .catch((err) => {
        console.log(err)
      })
    api.getInitialCards()
      .then((res)=>{
        setCards(res);
      })
      .catch((err)=>{
        console.log(err)
      })
  }, [])
  const cardElement = cards.map((card)=>(
    <Card 
      card = {card}
      key = {card._id}
      onCardClick = {onCardClick}
    />
  ))
  return (
    <main className="content">
      <section className="section-profile">
        <div className="profile">
          <div className="profile__info">
            <div className="profile__avatar">
              <img src={userAvatar} className="profile__image" onClick={onEditAvatar}/>
              <img
                src={ProfileImage}
                className="profile__vector-image"
              />
            </div>
            <div className="profile__account">
              <div className="profile__text">
                <h1 className="profile__title">{userName}</h1>
                <button
                  className="profile__redaction"
                  type="button"
                  aria-label="profile-redaction"
                  onClick={onEditProfile}
                >
                  <img
                    src={ProfileRedaction}
                    alt="Изменить профиль"
                    className="profile__redaction-image"
                  />
                </button>
              </div>
              <p className="profile__subtitle">{userDescription}</p>
            </div>
          </div>
          <button className="profile__add" type="button" onClick={onAddPlace}>
            <img src={AddPhoto} />
          </button>
        </div>
      </section>
      <section className="elements">{cardElement}</section>
    </main>

  );
}

export default Main;
