import React, { useState, useEffect } from 'react';
import { useContext } from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupClose from '../images/Close_Icon.svg';
import PopupWithForm from './PopupWithForm';
import api from '../utils/api';

function App() {
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);

  const [infoUser, setInfoUser] = useState({});
  const [cards, setCards] = useState();

  useEffect(() => {
    api.getUserProfile()
      .then((res) => {
        setInfoUser(res);
      })
      .catch((err) => {
        console.log(err)
      })
    api.getInitialCards()
      .then((res)=>{
        setCards
      })
      .catch((err)=>{
        console.log(err)
      })
  }, [])

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }
  function closeAllPopup() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
  }
  return (
    <div className="page">
      <Header />
      <Main
        onEditAvatar={handleEditAvatarClick}
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
      />
      <Footer />
      <PopupWithForm
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopup}
      />

      <div className="popup add-photo-popup">
        <div className="add-photo-popup__container">
          <button
            className="add-photo-popup__close"
            aria-label="add-photo-popup-close"
          >
            <img
              className="popup__close add-photo-popup__image"
              src={PopupClose}
              alt="Закрыть окно"
            />
          </button>
          <h3 className="add-photo-popup__title">Новое место</h3>
          <form
            name="popupFormAddImage"
            method="post"
            className="popup__form add-photo-popup__form"
            noValidate=""
          >
            <input
              id="name-foto-input"
              type="text"
              className="popup__input add-photo-popup__input add-photo-popup__input_type_photo"
              name="name"
              placeholder="Название"
              minLength={2}
              maxLength={40}
              required=""
            />
            <span className="name-foto-input-error" />
            <input
              id="link-photo-input"
              type="url"
              className="popup__input add-photo-popup__input add-photo-popup__input_type_link"
              name="link"
              placeholder="Ссылка на скачивание"
              required=""
            />
            <span className="link-photo-input-error" />
            <button className="popup__button add-photo-popup__save" type="submit">
              Сохранить
            </button>
          </form>
        </div>
      </div>

      <div className="popup photo-popup">
        <div className="photo-popup__container">
          <button className="photo-popup__close" aria-label="photo-popup-close">
            <img
              className="popup__close photo-popup__image"
              src={PopupClose}
              alt="Закрыть окно"
            />
          </button>
          <img alt="photo" className="photo-popup__photo" />
          <h2 className="photo-popup__text" />
        </div>
      </div>
      <div className="popup profile-image-popup">
        <div className="profile-image-popup__container">
          <button
            className="profile-image-popup__close"
            aria-label="profile-image-popup-close"
          >
            <img
              className="popup__close profile-image-popup__image"
              src="<%=require('./images/Close_Icon.svg')%>"
              alt="Закрыть окно"
            />
          </button>
          <h3 className="profile-image-popup__title">Обновить аватар</h3>
          <form
            name="popupFormProfileImage"
            method="post"
            className="popup__form profile-image-popup__form"
            noValidate=""
          >
            <input
              id="profile-image-popup"
              type="url"
              className="popup__input profile-image-popup__input profile-image-popup__input_type_link"
              name="avatar"
              placeholder="Ссылка на скачивание"
              required=""
            />
            <span className="profile-image-popup-error" />
            <button className="popup__button profile-image-popup__save" type="submit">
              Сохранить
            </button>
          </form>
        </div>
      </div>
      <div className="popup delete-photo-popup">
        <div className="delete-photo-popup__container">
          <button
            className="delete-photo-popup__close"
            aria-label="photo-popup-close"
          >
            <img
              className="popup__close delete-photo-popup__image"
              src={PopupClose}
              alt="Закрыть окно"
            />
          </button>
          <h3 className="delete-photo-popup__title">Вы уверены?</h3>
          <form
            name="popupFormDeletePhoto"
            method="post"
            className="popup__form delete-photo-popup__form"
            noValidate=""
          >
            <button className="popup__button delete-photo-popup__save" type="submit">
              Да
            </button>
          </form>
        </div>
      </div>



    </div>
  );
}

export default App;
